/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";

import { Formik } from "formik";
import * as Yup from "yup";
import { Form, Card, Col, Table, Row, Button } from "react-bootstrap";
import "./home-page.css";

import { FaEdit } from "react-icons/fa";
import { IoMdTrash } from "react-icons/io";

import { getAllUserService, addUserService, deleteUserService, updateUserService } from "../../services/Home-Page/homepage.service";
import swal from "sweetalert";

const HomePage = () => {
  const [state, setState] = useState({
    userInfoId: "",
    name: "",
    address: "",
    dateOfBirth: "",
    mobileNumber: "",
    userList: [],
  });
  const [loading, setLoading] = useState(true);

  const handleChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const tableSelectRow = (
    userInfoId,
    name,
    address,
    dateOfBirth,
    mobileNumber
  ) => {
    setState({
      ...state,
      userInfoId: userInfoId,
      name: name,
      address: address,
      dateOfBirth: dateOfBirth,
      mobileNumber: mobileNumber,
    });
  };

  const refreshState = () => {
    setState({
      ...state,
      userInfoId: "",
      name: "",
      address: "",
      dateOfBirth: "",
      mobileNumber: "",
    });
  };

  const updateUser = () => {
    let userData = {
      userInfoId: state.userInfoId,
      name: state.name,
      address: state.address,
      dateOfBirth: state.dateOfBirth,
      mobileNumber: state.mobileNumber,
    };
    updateUserService(userData).then((res) => {
      setLoading(true);
      swal({ icon: "success", title: "Updated!", timer: 2000 });
      refreshState();
    });
  };

  const deleteUser = (userInfoId) => {
    deleteUserService(userInfoId).then((res) => {
      setLoading(true);
      swal({ icon: "success", title: "Deleted!", timer: 2000 });
    });
  };

  const DataTable = () => {
    return state.userList.map((res, index) => {
      const { userInfoId, name, address, dateOfBirth, mobileNumber } = res;
      return (
        <tr key={index}>
          <td>{index + 1}</td>
          <td>{name}</td>
          <td>{address}</td>
          <td>{dateOfBirth}</td>
          <td>{mobileNumber}</td>
          <td style={{ padding: 10 }}>
            <FaEdit
              size={20}
              onClick={(i) =>
                tableSelectRow(
                  userInfoId,
                  name,
                  address,
                  dateOfBirth,
                  mobileNumber
                )
              }
            ></FaEdit>
          </td>
          <td style={{ padding: 10 }}>
            <IoMdTrash
              size={20}
              onClick={(i) => deleteUser(userInfoId)}
            ></IoMdTrash>
          </td>
        </tr>
      );
    });
  };

  useEffect(() => {
    getAllUserService().then((res) => {
      console.log(res);
      setLoading(false);
      const userList = res;
      setState({ ...state, userList });
    });
  }, [loading]);

  return (
    <div className="divPage">
      <Card>
        <Card.Header className="text-center">
          React-Formik-React_Bootstrap-NodeJs-Mysql
        </Card.Header>
        <Formik
          enableReinitialize={true}
          initialValues={state}
          validationSchema={Yup.object().shape({
            name: Yup.string()
              .min(1, "Name must be at least 3 characters")
              .max(100, "Name max 100 characters")
              .required("Name is required"),
            address: Yup.string()
              .min(1, "Address must be at least 3 characters")
              .max(100, "Address max 100 characters")
              .required("Address is required"),
            dateOfBirth: Yup.date().required("Date Of Birth is required"),
            mobileNumber: Yup.number()
              .positive("Phone number can't start with a minus")
              .integer("Phone number can't include a decimal point")
              .required("Phone Number is required"),
          })}
          onSubmit={(values) => {
            let userData = {
              name: values.name,
              address: values.address,
              dateOfBirth: values.dateOfBirth,
              mobileNumber: values.mobileNumber,
            };

            addUserService(userData).then((res) => {
              setLoading(true);
              swal({ icon: "success", title: "Added!", timer: 2000 });
              // refreshState();
            });
          }}
        >
          {(props) => (
            <Form onSubmit={props.handleSubmit} className="formPage">
              <Form.Group as={Row} controlId="formPlaintextName">
                <Form.Label column sm="2" className="formLabel">
                  Name
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={props.values.name}
                    onChange={props.handleChange && handleChange}
                    onBlur={props.handleBlur}
                    isInvalid={props.errors.name && props.touched.name}
                  />

                  <Form.Control.Feedback type="invalid">
                    {props.errors.name}
                  </Form.Control.Feedback>
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formPlaintextAddress">
                <Form.Label column sm="2" className="formLabel">
                  Address
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="text"
                    name="address"
                    placeholder="Address"
                    value={props.values.address}
                    onChange={props.handleChange && handleChange}
                    onBlur={props.handleBlur}
                    isInvalid={props.errors.address && props.touched.address}
                  />

                  <Form.Control.Feedback type="invalid">
                    {props.errors.address}
                  </Form.Control.Feedback>
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formPlaintextDateOfBirth">
                <Form.Label column sm="2" className="formLabel">
                  Date Of Birth
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="date"
                    name="dateOfBirth"
                    placeholder="YYYY-MM-DD"
                    value={props.values.dateOfBirth}
                    onChange={props.handleChange && handleChange}
                    onBlur={props.handleBlur}
                    isInvalid={
                      props.errors.dateOfBirth && props.touched.dateOfBirth
                    }
                  />

                  <Form.Control.Feedback type="invalid">
                    {props.errors.dateOfBirth}
                  </Form.Control.Feedback>
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formPlaintextMobileNumber">
                <Form.Label column sm="2" className="formLabel">
                  Mobile Number
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="tel"
                    name="mobileNumber"
                    placeholder="Mobile Number"
                    value={props.values.mobileNumber}
                    onChange={props.handleChange && handleChange}
                    onBlur={props.handleBlur}
                    isInvalid={
                      props.errors.mobileNumber && props.touched.mobileNumber
                    }
                  />

                  <Form.Control.Feedback type="invalid">
                    {props.errors.mobileNumber}
                  </Form.Control.Feedback>
                </Col>
              </Form.Group>
              <Button variant="primary" type="submit">
                Add User
              </Button>
              <Button
                variant="primary"
                onClick={updateUser}
                className="updateButton"
              >
                Update User
              </Button>
            </Form>
          )}
        </Formik>
      </Card>

      <Card className="cardForTable">
        <Card.Header className="text-center">User Details</Card.Header>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Address</th>
              <th>Date Of Birth</th>
              <th>Mobile Number</th>
            </tr>
          </thead>
          <tbody>{DataTable()}</tbody>
        </Table>
      </Card>
    </div>
  );
};

export default HomePage;
